mapper.select:
select id, create_at, creator_id, data_from, modify_at, points, reciever
from settlement
where id = #{id,jdbcType=BIGINT}

select id, create_at, creator_id, data_from, modify_at, points, reciever
from settlement
where reciever=#{receiver} order by create_at desc limit #{start},#{count}

       select r.id as userId,r.`name`,r.username,r.phone,s.points,s.create_at as createDate,u1.username as creator,r.phone,
			s.approve_time as approveTime,u2.username as approver,s.settle_time as settleTime,u3.username as settler,
			CASE when s.`status`=0 then '新申请' when s.`status`=1 then '已审核' when s.`status`=2 then '已结算' end as `status`

		from settlement s LEFT JOIN ruser r ON r.id=s.reciever
			LEFT JOIN `user` u1 ON u1.id=s.creator_id
			LEFT JOIN `user` u2 ON u2.id=s.approver_id
			LEFT JOIN `user` u3 ON u3.id=s.settler_id

			1=1

				and s.id=#{id}


               and r.id = #{value,jdbcType=VARCHAR}


            	and r.name LIKE CONCAT('%',#{value,jdbcType=VARCHAR},'%')


            	and r.phone LIKE CONCAT('%',#{value,jdbcType=VARCHAR},'%')


            	and s.status=#{status}


            	 AND DATE_FORMAT(s.create_at, '%Y-%m-%d')  >= #{startDate,jdbcType=TIMESTAMP} 


	             AND DATE_FORMAT(s.create_at, '%Y-%m-%d')  <= #{endDate,jdbcType=TIMESTAMP}

    	select s.id,s.create_at as createDate,s.creator_id as creatorId,s.approver_id as approverId,
    		s.approve_time as approveTime,s.settler_id as settlerId,s.settle_time as settleTime,
			s.`status`,s.points,s.reciever as userId,r.`name`,r.username,r.phone 
		from settlement s LEFT JOIN ruser r ON s.reciever=r.id 

			1=1

               and r.id = #{value,jdbcType=VARCHAR}


            	and r.name LIKE CONCAT('%',#{value,jdbcType=VARCHAR},'%')


            	and r.phone LIKE CONCAT('%',#{value,jdbcType=VARCHAR},'%')


            	and s.status=#{status}


            	 AND DATE_FORMAT(s.create_at, '%Y-%m-%d')  >= #{startDate,jdbcType=TIMESTAMP} 


	             AND DATE_FORMAT(s.create_at, '%Y-%m-%d')  <= #{endDate,jdbcType=TIMESTAMP}

    	select sum(s.points) 
		from settlement s LEFT JOIN ruser r ON s.reciever=r.id 

			1=1

               and r.id = #{value,jdbcType=VARCHAR}


            	and r.name LIKE CONCAT('%',#{value,jdbcType=VARCHAR},'%')


            	and r.phone LIKE CONCAT('%',#{value,jdbcType=VARCHAR},'%')


            	and s.status=#{status}


            	 AND DATE_FORMAT(s.create_at, '%Y-%m-%d')  >= #{startDate,jdbcType=TIMESTAMP} 


	             AND DATE_FORMAT(s.create_at, '%Y-%m-%d')  <= #{endDate,jdbcType=TIMESTAMP}
